mapper.select:
select c.id,c.fullName,v1.value as appellation,u.name as owner, c.company,c.phone,c.mPhone,v2.value as state,v3.value as source from tbl_clue c join tbl_user u on u.id = c.owner left join tbl_dic_value v1 on v1.id = c.appellation left join tbl_dic_value v2 on v2.id = c.state left join tbl_dic_value v3 on v3.id = c.source
and u.name like concat('%', #{owner},'%')
and c.fullName like concat('%', #{fullName},'%')
and c.company like concat('%', #{company},'%')
and c.phone like concat('%', #{phone},'%')
and c.mPhone like concat('%', #{mPhone},'%')
and c.source = #{source}
and c.state = #{state}
order by c.editTime desc Limit ${pageNo},${pageSize}

mapper.update:
update tbl_clue set fullName = #{fullName}, appellation = #{appellation}, owner = #{owner}, company=#{company}, job=#{job}, email=#{email}, phone=#{phone}, website=#{website}, mPhone=#{mPhone}, state=#{state},
                    source=#{source},editBy=#{editBy}, editTime=#{editTime}, description=#{description}, contactSummary=#{contactSummary}, nextContactTime=#{nextContactTime},
                    address=#{address}
where id = #{id};

mapper.delete:
delete from tbl_clue
where id in

  #{id}
