mapper.select:
select c.id,u1.name as owner,v1.value as source,cs.name as customerId,c.fullname,v2.value as appellation, c.email,c.mphone,c.job,c.birth,u2.name as createBy,c.createTime,u3.name as editBy,c.editTime, c.description,c.contactSummary,c.nextContactTime,c.address from tbl_contacts c join tbl_user u1 on u1.id = c.owner join tbl_dic_value v1 on v1.id = c.source left join tbl_customer cs on cs.id = c.customerId join tbl_dic_value v2 on v2.id = c.appellation join tbl_user u2 on u2.id = c.createBy join tbl_user u3 on u3.id = c.editBy
and u1.name like concat('%',#{owner},'%')
and c.source = #{source}
and cs.name like concat('%',#{customerId},'%')
and c.birth = #{birth}
and c.fullname like concat('%',#{fullName},'%')
order by cs.name desc Limit ${pageNo},${pageSize};

select count(*) from tbl_contacts c join tbl_user u1 on u1.id = c.owner join tbl_dic_value v1 on v1.id = c.source left join tbl_customer cs on cs.id = c.customerId join tbl_dic_value v2 on v2.id = c.appellation join tbl_user u2 on u2.id = c.createBy join tbl_user u3 on u3.id = c.editBy
and u1.name like concat('%',#{owner},'%')
and c.source = #{source}
and cs.name like concat('%',#{customerId},'%')
and c.birth = #{birth}
and c.fullname like concat('%',#{fullName},'%')

mapper.delete:
delete from tbl_contacts
where id in

  #{id}
